import React, { useState, useEffect, useMemo } from 'react';
import { Search, Package, MapPin, CheckCircle, AlertCircle, Sprout, Factory, Truck, Store, ShoppingBag, Loader, ArrowRight, ChevronDown } from 'lucide-react';
import { API } from '../config/api';

const stageConfigs = [
  { 
    key: 'packet', 
    title: 'Packet Info', 
    fullTitle: 'Packet Information',
    icon: Package, 
    gradient: 'from-slate-600 to-slate-800',
    lightBg: 'bg-slate-50',
    textColor: 'text-slate-700',
    ringColor: 'ring-slate-400'
  },
  { 
    key: 'harvest', 
    title: 'Harvest', 
    fullTitle: 'Harvest Details',
    icon: Sprout, 
    gradient: 'from-green-500 to-emerald-600',
    lightBg: 'bg-green-50',
    textColor: 'text-green-700',
    ringColor: 'ring-green-400'
  },
  { 
    key: 'processing', 
    title: 'Processing', 
    fullTitle: 'Processing & Quality',
    icon: Factory, 
    gradient: 'from-violet-500 to-purple-600',
    lightBg: 'bg-violet-50',
    textColor: 'text-violet-700',
    ringColor: 'ring-violet-400'
  },
  { 
    key: 'distributor', 
    title: 'Distribution', 
    fullTitle: 'Distribution',
    icon: Truck, 
    gradient: 'from-amber-500 to-orange-600',
    lightBg: 'bg-amber-50',
    textColor: 'text-amber-700',
    ringColor: 'ring-amber-400'
  },
  { 
    key: 'supplier', 
    title: 'Supplier', 
    fullTitle: 'Supplier',
    icon: Store, 
    gradient: 'from-rose-500 to-pink-600',
    lightBg: 'bg-rose-50',
    textColor: 'text-rose-700',
    ringColor: 'ring-rose-400'
  },
  { 
    key: 'shopkeeper', 
    title: 'Retail', 
    fullTitle: 'Retail Store',
    icon: ShoppingBag, 
    gradient: 'from-blue-500 to-indigo-600',
    lightBg: 'bg-blue-50',
    textColor: 'text-blue-700',
    ringColor: 'ring-blue-400'
  },
];

const TrackingPage = () => {
  const [packetId, setPacketId] = useState('');
  const [journeyData, setJourneyData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [selectedStage, setSelectedStage] = useState(null);
  const [verificationSteps, setVerificationSteps] = useState([]);
  const [currentStep, setCurrentStep] = useState(-1);

  const verificationSequence = [
    'Data pushed to Blockchain',
    'Fetched from Blockchain',
    'Fetched from Database',
    'Hash generated',
    'Hash Matched',
    'Data Verified'
  ];

  useEffect(() => {
    if (loading) {
      setVerificationSteps([]);
      setCurrentStep(-1);
      const interval = setInterval(() => {
        setCurrentStep(prev => {
          if (prev < verificationSequence.length - 1) {
            setVerificationSteps(prevSteps => [...prevSteps, verificationSequence[prev + 1]]);
            return prev + 1;
          }
          return prev;
        });
      }, 400);
      return () => clearInterval(interval);
    }
  }, [loading]);

  const handleTrack = async () => {
    if (!packetId.trim()) {
      setError('Please enter a packet ID');
      return;
    }
    setLoading(true);
    setError('');
    setJourneyData(null);
    setSelectedStage(null);

    try {
      const res = await API.getJourney(packetId);
      setJourneyData(res.data);
      // Auto-select first available stage
      const firstStage = stageConfigs.find(config => res.data[config.key]);
      if (firstStage) {
        setSelectedStage(firstStage.key);
      }
    } catch (err) {
      setError(err.response?.data?.error || 'Failed to fetch tracking info');
      setJourneyData(null);
    } finally {
      setTimeout(() => setLoading(false), 2800);
    }
  };

  const mapLink = (value) => {
    if (!value) return null;
    const parts = value.toString().split(',').map(v => v.trim());
    if (parts.length !== 2) return null;
    const [lat, lng] = parts;
    return `https://www.google.com/maps?q=${lat},${lng}`;
  };

  const renderDetails = (obj) => (
    <div className="space-y-3">
      {Object.entries(obj).map(([key, val]) => {
        if (!val || typeof val === 'object') return null;
        const isGps = key.toLowerCase().includes('gps') || key.toLowerCase().includes('coordinates') || key.toLowerCase().includes('location');
        const link = isGps ? mapLink(val) : null;
        
        return (
          <div key={key} className="flex justify-between items-start py-3 border-b border-gray-200 last:border-0">
            <span className="text-sm font-semibold text-slate-600 uppercase tracking-wide">
              {key.replace(/_/g, ' ')}
            </span>
            {isGps && link ? (
              <a 
                href={link} 
                target="_blank" 
                rel="noreferrer" 
                className="text-emerald-600 hover:text-emerald-700 font-semibold inline-flex items-center gap-1 transition-colors"
              >
                <MapPin className="w-4 h-4" />
                <span className="underline">{val.toString()}</span>
              </a>
            ) : (
              <span className="text-slate-900 font-semibold text-right max-w-xs break-words">{val.toString()}</span>
            )}
          </div>
        );
      })}
    </div>
  );

  const timeline = useMemo(() => {
    if (!journeyData) return [];
    return stageConfigs.map(s => ({
      ...s,
      done: Boolean(journeyData[s.key])
    }));
  }, [journeyData]);

  const StageCard = ({ config, index }) => {
    if (!journeyData || !journeyData[config.key]) return null;
    
    const Icon = config.icon;
    const isSelected = selectedStage === config.key;

    return (
      <div 
        onClick={() => setSelectedStage(isSelected ? null : config.key)}
        className={`cursor-pointer transition-all duration-300 rounded-2xl ${
          isSelected 
            ? `ring-4 ${config.ringColor} ring-offset-2 shadow-2xl` 
            : 'hover:shadow-xl shadow-md'
        }`}
        style={{ 
          animation: 'slideUp 0.5s ease-out forwards',
          animationDelay: `${index * 100}ms`,
          opacity: 0
        }}
      >
        <div className={`bg-gradient-to-br ${config.gradient} px-6 py-5 flex items-center gap-4 ${isSelected ? 'rounded-t-2xl' : 'rounded-2xl'}`}>
          <div className="bg-white bg-opacity-20 backdrop-blur-sm p-3 rounded-xl">
            <Icon className="w-7 h-7 text-white" />
          </div>
          <div className="flex-1">
            <h3 className="text-xl font-bold text-white">{config.fullTitle}</h3>
          </div>
          <div className="flex items-center gap-2">
            <div className="bg-white bg-opacity-25 backdrop-blur-sm px-4 py-2 rounded-full">
              <span className="text-sm font-bold text-white">Stage {index + 1}</span>
            </div>
            <ChevronDown className={`w-6 h-6 text-white transition-transform duration-300 ${isSelected ? 'rotate-180' : ''}`} />
          </div>
        </div>
        {isSelected && (
          <div className={`${config.lightBg} p-6 border-x-2 border-b-2 ${config.ringColor.replace('ring', 'border')} rounded-b-2xl`}
            style={{
              animation: 'expandDown 0.3s ease-out forwards'
            }}
          >
            {renderDetails(journeyData[config.key])}
          </div>
        )}
      </div>
    );
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 via-gray-50 to-slate-100 py-8 px-4">
      {/* Header */}
      <div className="text-center mb-10">
        <h1 className="text-5xl font-extrabold bg-gradient-to-r from-slate-800 to-slate-600 bg-clip-text text-transparent mb-3">
          Track Your Product
        </h1>
        <p className="text-slate-600 text-lg">Enter packet ID to trace the complete supply chain journey</p>
      </div>

      {/* Search Box */}
      <div className="max-w-3xl mx-auto mb-10">
        <div className="bg-white rounded-2xl shadow-xl p-6 border border-slate-200">
          <div className="flex flex-col md:flex-row gap-4">
            <input
              type="text"
              value={packetId}
              onChange={(e) => setPacketId(e.target.value)}
              onKeyPress={(e) => e.key === 'Enter' && handleTrack()}
              placeholder="Enter Packet ID"
              className="flex-1 px-6 py-4 border-2 border-slate-300 rounded-xl focus:border-emerald-500 focus:outline-none text-lg transition-colors"
            />
            <button 
              onClick={handleTrack} 
              disabled={loading}
              className="px-10 py-4 bg-gradient-to-r from-emerald-500 to-green-600 text-white rounded-xl hover:from-emerald-600 hover:to-green-700 disabled:from-gray-400 disabled:to-gray-500 disabled:cursor-not-allowed flex items-center justify-center gap-2 transition-all font-semibold text-lg shadow-lg hover:shadow-xl"
            >
              <Search className="w-5 h-5" />
              Track
            </button>
          </div>
          
          {error && (
            <div className="mt-4 p-4 bg-red-50 border-2 border-red-300 rounded-xl flex items-center gap-3 text-red-700">
              <AlertCircle className="w-5 h-5 flex-shrink-0" />
              <span className="font-medium">{error}</span>
            </div>
          )}
        </div>
      </div>

      {/* Results */}
      {journeyData && !loading && (
        <div className="max-w-6xl mx-auto space-y-8">
          {/* Verification Badge */}
          <div className="bg-white rounded-2xl shadow-xl p-6 border-2 border-emerald-300">
            <div className="flex items-center gap-3 text-emerald-600">
              <div className="bg-emerald-100 p-2 rounded-lg">
                <CheckCircle className="w-7 h-7" />
              </div>
              <span className="font-bold text-xl">Journey Verified on Blockchain</span>
            </div>
          </div>

          {/* Timeline */}
          <div className="bg-white rounded-2xl shadow-xl p-8 border border-slate-200">
            <h2 className="text-2xl font-bold text-slate-800 mb-6">Supply Chain Timeline</h2>
            <div className="flex items-center justify-between overflow-x-auto pb-4 gap-2">
              {timeline.map((stage, idx) => {
                const Icon = stage.icon;
                const isActive = selectedStage === stage.key;
                
                return (
                  <React.Fragment key={stage.key}>
                    <div 
                      onClick={() => stage.done && setSelectedStage(stage.key)}
                      className={`flex flex-col items-center min-w-[100px] group ${stage.done ? 'cursor-pointer' : 'cursor-not-allowed'}`}
                    >
                      <div className={`w-16 h-16 rounded-2xl flex items-center justify-center mb-3 transition-all shadow-md ${
                        stage.done 
                          ? isActive
                            ? `bg-gradient-to-br ${stage.gradient} scale-110 shadow-xl`
                            : `bg-gradient-to-br ${stage.gradient} hover:scale-105 group-hover:shadow-lg`
                          : 'bg-slate-200'
                      }`}>
                        <Icon className={`w-8 h-8 ${stage.done ? 'text-white' : 'text-slate-400'}`} />
                      </div>
                      <span className={`text-sm font-bold text-center transition-colors ${
                        stage.done 
                          ? isActive 
                            ? stage.textColor
                            : 'text-slate-700 group-hover:' + stage.textColor
                          : 'text-slate-400'
                      }`}>
                        {stage.title}
                      </span>
                    </div>
                    {idx < timeline.length - 1 && (
                      <div className="flex-1 mx-2 min-w-[40px]">
                        <div className={`h-2 rounded-full transition-all ${
                          stage.done && timeline[idx + 1].done 
                            ? 'bg-gradient-to-r from-emerald-500 to-green-500' 
                            : 'bg-slate-200'
                        }`}></div>
                      </div>
                    )}
                  </React.Fragment>
                );
              })}
            </div>
          </div>

          {/* Instructions */}
          <div className="bg-gradient-to-r from-blue-50 to-indigo-50 rounded-2xl p-6 border border-blue-200">
            <div className="flex items-start gap-3">
              <div className="bg-blue-500 p-2 rounded-lg mt-1">
                <ArrowRight className="w-5 h-5 text-white" />
              </div>
              <div>
                <h3 className="font-bold text-slate-800 text-lg mb-1">Click to View Details</h3>
                <p className="text-slate-600">Select any stage from the timeline or cards below to expand and see complete information</p>
              </div>
            </div>
          </div>

          {/* Stage Cards */}
          <div className="space-y-4">
            {stageConfigs.map((config, index) => (
              <StageCard key={config.key} config={config} index={index} />
            ))}
          </div>
        </div>
      )}

      {/* Empty State */}
      {!journeyData && !loading && !error && (
        <div className="max-w-3xl mx-auto text-center py-20 bg-white rounded-2xl shadow-xl border border-slate-200">
          <div className="bg-slate-100 w-24 h-24 rounded-2xl flex items-center justify-center mx-auto mb-6">
            <Package className="w-12 h-12 text-slate-400" />
          </div>
          <h3 className="text-2xl font-bold text-slate-800 mb-2">Ready to Track</h3>
          <p className="text-slate-600 text-lg">Enter a packet ID above to start tracking your product's journey</p>
        </div>
      )}

      {/* Verification Overlay */}
      {loading && (
        <div className="fixed inset-0 bg-gradient-to-br from-slate-900 via-slate-800 to-slate-900 bg-opacity-95 flex items-center justify-center z-50 backdrop-blur-sm">
          <div className="max-w-md w-full mx-4">
            <div className="text-center mb-8">
              <div className="inline-block p-4 bg-emerald-500 bg-opacity-20 backdrop-blur-sm rounded-full mb-4">
                <Loader className="w-12 h-12 text-emerald-400 animate-spin" />
              </div>
              <h2 className="text-2xl font-bold text-white mb-2">Verifying on Blockchain...</h2>
              <p className="text-slate-300">Tracking packet: <span className="font-semibold text-emerald-400">{packetId}</span></p>
            </div>

            <div className="space-y-3">
              {verificationSteps.map((step, index) => (
                <div 
                  key={index}
                  className="flex items-start gap-3 p-4 bg-white bg-opacity-10 backdrop-blur-md rounded-xl border border-white border-opacity-20"
                  style={{
                    animation: 'fadeIn 0.3s ease-out forwards',
                    opacity: 0
                  }}
                >
                  <div className="flex-shrink-0 mt-0.5">
                    <CheckCircle className="w-6 h-6 text-emerald-400" />
                  </div>
                  <p className="text-white font-medium">{step}</p>
                </div>
              ))}
            </div>

            {currentStep === verificationSequence.length - 1 && (
              <button
                className="w-full mt-6 px-6 py-4 bg-gradient-to-r from-emerald-500 to-green-600 text-white rounded-xl font-bold text-lg hover:from-emerald-600 hover:to-green-700 transition-all shadow-lg"
                style={{
                  animation: 'fadeIn 0.3s ease-out forwards',
                  opacity: 0,
                  animationDelay: '200ms'
                }}
              >
                Trace successfully verified
              </button>
            )}
          </div>
        </div>
      )}

      <style>{`
        @keyframes slideUp {
          from {
            opacity: 0;
            transform: translateY(20px);
          }
          to {
            opacity: 1;
            transform: translateY(0);
          }
        }
        
        @keyframes fadeIn {
          from {
            opacity: 0;
          }
          to {
            opacity: 1;
          }
        }

        @keyframes expandDown {
          from {
            opacity: 0;
            max-height: 0;
          }
          to {
            opacity: 1;
            max-height: 1000px;
          }
        }
      `}</style>
    </div>
  );
};

export default TrackingPage;
