import React, { useState } from 'react';
import { Save, AlertCircle, MapPin } from 'lucide-react';

// Mock constants for demo
const FORM_FIELDS = {
  HARVEST: [
    { name: 'farmer_name', label: 'Farmer Name', type: 'text', required: true },
    { name: 'farm_location', label: 'Farm Location', type: 'text', required: true },
    { name: 'gps_coordinates', label: 'GPS Coordinates', type: 'text', required: false },
    { name: 'harvest_date', label: 'Harvest Date', type: 'date', required: true },
    { name: 'quantity', label: 'Quantity (kg)', type: 'number', required: true },
  ],
  PROCESSING: [
    { name: 'processor_name', label: 'Processor Name', type: 'text', required: true },
    { name: 'gps_coordinates', label: 'GPS Coordinates', type: 'text', required: false },
    { name: 'processing_date', label: 'Processing Date', type: 'date', required: true },
    { name: 'moisture_content', label: 'Moisture Content (%)', type: 'number', required: false },
  ],
};

const ROLES = [
  { id: 'farmer', name: 'Farmer', description: 'Record harvest data', color: 'green', icon: MapPin },
  { id: 'processor', name: 'Processor', description: 'Record processing data', color: 'blue', icon: MapPin },
];

const DataEntryForm = ({ userRole = 'farmer' }) => {
  const [formData, setFormData] = useState({});
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [gpsLoading, setGpsLoading] = useState(false);

  const getFormFields = () => {
    switch (userRole) {
      case 'farmer': return FORM_FIELDS.HARVEST;
      case 'processor': return FORM_FIELDS.PROCESSING;
      default: return FORM_FIELDS.HARVEST;
    }
  };

  const getRoleInfo = () => {
    return ROLES.find(role => role.id === userRole);
  };

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
    setError('');
  };

  // GPS location function
  const getGPSLocation = () => {
    setGpsLoading(true);
    setError('');

    if (!navigator.geolocation) {
      setError('Geolocation is not supported by your browser');
      setGpsLoading(false);
      return;
    }

    navigator.geolocation.getCurrentPosition(
      (position) => {
        const latitude = position.coords.latitude.toFixed(6);
        const longitude = position.coords.longitude.toFixed(6);
        const coordinates = `${latitude}, ${longitude}`;
        
        setFormData(prev => ({
          ...prev,
          gps_coordinates: coordinates
        }));
        setSuccess('Location detected successfully!');
        setTimeout(() => setSuccess(''), 2000);
        setGpsLoading(false);
      },
      (error) => {
        let errorMessage = 'Unable to retrieve your location';
        switch(error.code) {
          case error.PERMISSION_DENIED:
            errorMessage = 'Location access denied. Please enable location permissions.';
            break;
          case error.POSITION_UNAVAILABLE:
            errorMessage = 'Location information is unavailable.';
            break;
          case error.TIMEOUT:
            errorMessage = 'Location request timed out.';
            break;
          default:
            errorMessage = 'An unknown error occurred.';
        }
        setError(errorMessage);
        setGpsLoading(false);
      },
      {
        enableHighAccuracy: true,
        timeout: 10000,
        maximumAge: 0
      }
    );
  };

  const validateForm = () => {
    const fields = getFormFields();
    for (let field of fields) {
      if (field.required && !formData[field.name]) {
        setError(`${field.label} is required`);
        return false;
      }
    }
    return true;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!validateForm()) {
      return;
    }

    setLoading(true);
    setError('');
    setSuccess('');

    try {
      // Simulate API call
      await new Promise(resolve => setTimeout(resolve, 1000));
      
      setSuccess('Data submitted successfully!');
      setFormData({});
      setTimeout(() => setSuccess(''), 3000);
    } catch (err) {
      setError('Failed to submit data');
    } finally {
      setLoading(false);
    }
  };

  const roleInfo = getRoleInfo();
  const fields = getFormFields();
  const IconComponent = roleInfo?.icon;

  if (!roleInfo) {
    return null;
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-green-50 to-blue-50 p-4">
      <div className="max-w-4xl mx-auto">
        <div className="bg-white rounded-lg shadow-lg p-6">
          <div className="flex items-center gap-3 mb-6">
            {IconComponent && (
              <div className="bg-green-100 p-3 rounded-lg">
                <IconComponent className="text-green-600" size={24} />
              </div>
            )}
            <div>
              <h2 className="text-2xl font-bold text-gray-800">
                {roleInfo.name} Data Entry
              </h2>
              <p className="text-sm text-gray-500">{roleInfo.description}</p>
            </div>
          </div>

          {error && (
            <div className="mb-4 p-4 bg-red-50 border-l-4 border-red-500 rounded flex items-start gap-2">
              <AlertCircle className="text-red-500 mt-0.5" size={20} />
              <div>
                <p className="font-medium text-red-800">Error</p>
                <p className="text-sm text-red-600">{error}</p>
              </div>
            </div>
          )}

          {success && (
            <div className="mb-4 p-4 bg-green-50 border-l-4 border-green-500 rounded flex items-start gap-2">
              <AlertCircle className="text-green-500 mt-0.5" size={20} />
              <div>
                <p className="font-medium text-green-800">Success</p>
                <p className="text-sm text-green-600">{success}</p>
              </div>
            </div>
          )}

          <form onSubmit={handleSubmit}>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              {fields.map(field => (
                <div 
                  key={field.name} 
                  className={field.type === 'textarea' ? 'md:col-span-2' : ''}
                >
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    {field.label}
                    {field.required && <span className="text-red-500 ml-1">*</span>}
                  </label>
                  
                  {field.name === 'gps_coordinates' ? (
                    <div className="flex gap-2">
                      <input
                        type="text"
                        name={field.name}
                        value={formData[field.name] || ''}
                        onChange={handleChange}
                        className="flex-1 border border-gray-300 rounded-lg px-4 py-2 focus:ring-2 focus:ring-green-500 focus:border-transparent outline-none transition"
                        placeholder="Click button to get location"
                        readOnly
                      />
                      <button
                        type="button"
                        onClick={getGPSLocation}
                        disabled={gpsLoading}
                        className="bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700 transition-colors duration-200 flex items-center gap-2 font-medium disabled:opacity-50 disabled:cursor-not-allowed whitespace-nowrap"
                      >
                        {gpsLoading ? (
                          <>
                            <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-white"></div>
                            <span>Getting...</span>
                          </>
                        ) : (
                          <>
                            <MapPin size={20} />
                            <span>Get Location</span>
                          </>
                        )}
                      </button>
                    </div>
                  ) : field.type === 'textarea' ? (
                    <textarea
                      name={field.name}
                      value={formData[field.name] || ''}
                      onChange={handleChange}
                      className="w-full border border-gray-300 rounded-lg px-4 py-2 focus:ring-2 focus:ring-green-500 focus:border-transparent outline-none transition"
                      rows="3"
                      placeholder={field.label}
                    />
                  ) : field.type === 'checkbox' ? (
                    <div className="flex items-center">
                      <input
                        type="checkbox"
                        name={field.name}
                        checked={formData[field.name] || false}
                        onChange={handleChange}
                        className="w-5 h-5 text-green-600 border-gray-300 rounded focus:ring-green-500"
                      />
                      <span className="ml-2 text-sm text-gray-600">
                        Mark as organic certified
                      </span>
                    </div>
                  ) : (
                    <input
                      type={field.type}
                      name={field.name}
                      value={formData[field.name] || ''}
                      onChange={handleChange}
                      className="w-full border border-gray-300 rounded-lg px-4 py-2 focus:ring-2 focus:ring-green-500 focus:border-transparent outline-none transition"
                      placeholder={field.label}
                    />
                  )}
                </div>
              ))}
            </div>

            <button
              type="submit"
              disabled={loading}
              className="mt-6 w-full md:w-auto bg-green-600 text-white px-8 py-3 rounded-lg hover:bg-green-700 transition-colors duration-200 flex items-center justify-center gap-2 font-medium disabled:opacity-50 disabled:cursor-not-allowed"
            >
              {loading ? (
                <>
                  <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-white"></div>
                  <span>Submitting...</span>
                </>
              ) : (
                <>
                  <Save size={20} />
                  <span>Submit</span>
                </>
              )}
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default DataEntryForm;
