import React, { useState, useEffect, useMemo } from 'react';
import { Search, Package, MapPin, CheckCircle, AlertCircle, Sprout, Factory, Truck, Store, ShoppingBag, Loader } from 'lucide-react';
import { API } from '../config/api';

const stageConfigs = [
  { key: 'packet', title: 'Packet Info', icon: Package, color: 'slate' },
  { key: 'harvest', title: 'Harvest', icon: Sprout, color: 'green' },
  { key: 'processing', title: 'Processing', icon: Factory, color: 'violet' },
  { key: 'distributor', title: 'Distribution', icon: Truck, color: 'amber' },
  { key: 'supplier', title: 'Supplier', icon: Store, color: 'rose' },
  { key: 'shopkeeper', title: 'Retail', icon: ShoppingBag, color: 'blue' },
];

const TrackingPage = () => {
  const [packetId, setPacketId] = useState('');
  const [journeyData, setJourneyData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [selectedStage, setSelectedStage] = useState(null);
  const [verificationSteps, setVerificationSteps] = useState([]);
  const [currentStep, setCurrentStep] = useState(-1);

  const verificationSequence = [
    'Data pushed to Blockchain',
    'Fetched from Blockchain',
    'Fetched from Database',
    'Hash generated',
    'Hash Matched',
    'Data Verified'
  ];

  useEffect(() => {
    if (loading) {
      setVerificationSteps([]);
      setCurrentStep(-1);
      const interval = setInterval(() => {
        setCurrentStep(prev => {
          if (prev < verificationSequence.length - 1) {
            setVerificationSteps(prevSteps => [...prevSteps, verificationSequence[prev + 1]]);
            return prev + 1;
          }
          return prev;
        });
      }, 400);
      return () => clearInterval(interval);
    }
  }, [loading]);

  const handleTrack = async () => {
    if (!packetId.trim()) {
      setError('Please enter a packet ID');
      return;
    }
    setLoading(true);
    setError('');
    setJourneyData(null);
    setSelectedStage(null);

    try {
      const res = await API.getJourney(packetId);
      setJourneyData(res.data);
    } catch (err) {
      setError(err.response?.data?.error || 'Failed to fetch tracking info');
      setJourneyData(null);
    } finally {
      setTimeout(() => setLoading(false), 2800);
    }
  };

  const mapLink = (value) => {
    if (!value) return null;
    const [lat, lng] = value.split(',').map(v => v.trim());
    return `https://www.google.com/maps?q=${lat},${lng}`;
  };

  const renderDetails = (obj) => (
    <div className="space-y-2">
      {Object.entries(obj).map(([key, val]) => {
        if (!val || typeof val === 'object') return null;
        const isGps = key.toLowerCase().includes('gps') || key.toLowerCase().includes('location');
        return (
          <div key={key} className="flex justify-between text-sm font-medium">
            <span className="text-gray-600">{key.replace(/_/g, ' ')}</span>
            {isGps ? (
              <a href={mapLink(val)} target="_blank" rel="noreferrer" className="text-emerald-600 underline flex items-center gap-1">
                <MapPin className="w-4 h-4" /> {val}
              </a>
            ) : (
              <span className="text-gray-900">{val}</span>
            )}
          </div>
        );
      })}
    </div>
  );

  const timeline = useMemo(() => {
    if (!journeyData) return [];
    return stageConfigs.map(s => ({
      ...s,
      done: Boolean(journeyData[s.key])
    }));
  }, [journeyData]);

  return (
    <div className="min-h-screen bg-gray-50 py-8 px-4">
      {/* Header */}
      <div className="text-center mb-10">
        <h1 className="text-4xl font-extrabold text-gray-800 mb-2">Track Your Product</h1>
        <p className="text-gray-600">Enter a packet ID to trace the supply chain journey</p>
      </div>

      {/* Search */}
      <div className="max-w-2xl mx-auto flex gap-2 mb-8">
        <input
          type="text"
          value={packetId}
          onChange={(e) => setPacketId(e.target.value)}
          onKeyPress={(e) => e.key === 'Enter' && handleTrack()}
          placeholder="Enter Packet ID"
          className="flex-1 px-4 py-3 border rounded-xl focus:outline-none focus:ring-2 focus:ring-emerald-500 text-lg"
        />
        <button onClick={handleTrack} className="px-6 py-3 bg-emerald-500 text-white rounded-xl hover:bg-emerald-600 flex items-center gap-2">
          <Search className="w-5 h-5" /> Track
        </button>
      </div>

      {error && (
        <div className="max-w-2xl mx-auto mb-6 p-4 bg-red-50 border border-red-300 rounded-xl flex items-center gap-2 text-red-700">
          <AlertCircle className="w-5 h-5" /> {error}
        </div>
      )}

      {/* Timeline */}
      {journeyData && (
        <div className="max-w-5xl mx-auto mb-8 flex gap-4 overflow-x-auto py-4">
          {timeline.map((s, idx) => {
            const Icon = s.icon;
            return (
              <div
                key={s.key}
                className={`flex flex-col items-center min-w-[90px] cursor-pointer`}
                onClick={() => s.done && setSelectedStage(selectedStage === s.key ? null : s.key)}
              >
                <div className={`w-16 h-16 rounded-xl flex items-center justify-center ${s.done ? 'bg-gradient-to-br from-emerald-500 to-green-500 text-white' : 'bg-gray-200 text-gray-400'}`}>
                  <Icon className="w-7 h-7" />
                </div>
                <span className={`mt-2 text-center text-sm font-semibold ${s.done ? 'text-gray-800' : 'text-gray-400'}`}>{s.title}</span>
              </div>
            );
          })}
        </div>
      )}

      {/* Stage Details */}
      {selectedStage && journeyData[selectedStage] && (
        <div className="max-w-3xl mx-auto p-6 bg-white rounded-2xl shadow-lg border border-gray-200">
          <h3 className="text-xl font-bold mb-4">{stageConfigs.find(s => s.key === selectedStage)?.title}</h3>
          {renderDetails(journeyData[selectedStage])}
        </div>
      )}

      {/* Verification Overlay */}
      {loading && (
        <div className="fixed inset-0 bg-black bg-opacity-60 flex items-center justify-center z-50">
          <div className="bg-gray-800 p-6 rounded-2xl w-96 text-center space-y-4">
            <Loader className="w-12 h-12 text-emerald-400 animate-spin mx-auto" />
            <h2 className="text-white font-bold text-lg">Verifying on Blockchain...</h2>
            <p className="text-gray-300">{packetId}</p>
            <div className="space-y-2">
              {verificationSteps.map((v, i) => (
                <div key={i} className="flex items-center gap-2 text-white">
                  <CheckCircle className="w-5 h-5 text-emerald-400" /> {v}
                </div>
              ))}
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default TrackingPage;
