import React, { useState } from 'react';
import { MessageCircle, X } from 'lucide-react';

const ChatbotWidget = () => {
  const [isOpen, setIsOpen] = useState(false);

  const toggleChatbot = () => {
    setIsOpen(!isOpen);
  };

  return (
    <div className="fixed bottom-6 right-6 z-50">
      {/* Chatbot Container */}
      {isOpen && (
        <div className="mb-4 bg-white rounded-lg shadow-2xl border border-gray-200 overflow-hidden animate-slide-up">
          <div className="bg-gradient-to-r from-blue-600 to-blue-700 px-4 py-3 flex items-center justify-between">
            <div className="flex items-center gap-2">
              <MessageCircle className="text-white" size={20} />
              <h3 className="text-white font-semibold">Need Help?</h3>
            </div>
            <button
              onClick={toggleChatbot}
              className="text-white hover:bg-blue-800 rounded-full p-1 transition-colors"
              aria-label="Close chatbot"
            >
              <X size={20} />
            </button>
          </div>
          <div className="w-[400px] h-[600px] bg-white">
            <iframe
              src="https://landbot.online/v3/H-3337203-VVIJPE3ZMLNBWXO7/index.html"
              title="Turmeric Supply Chain Chatbot"
              className="w-full h-full border-0"
              allow="microphone"
              loading="lazy"
            />
          </div>
        </div>
      )}

      {/* Floating Button */}
      <button
        onClick={toggleChatbot}
        className={`bg-gradient-to-r from-blue-600 to-blue-700 text-white rounded-full p-4 shadow-lg hover:shadow-xl transition-all duration-300 hover:scale-110 flex items-center justify-center ${
          isOpen ? 'rotate-180' : ''
        }`}
        aria-label={isOpen ? 'Close chatbot' : 'Open chatbot'}
      >
        {isOpen ? (
          <X size={24} />
        ) : (
          <MessageCircle size={24} />
        )}
      </button>

      {/* Pulse animation when closed */}
      {!isOpen && (
        <div className="absolute inset-0 bg-blue-600 rounded-full animate-ping opacity-75"></div>
      )}
    </div>
  );
};

export default ChatbotWidget;
