import React, { useMemo, useState, useEffect } from 'react';
import { Search, Package, MapPin, CheckCircle, AlertCircle, Sprout, Factory, Truck, Store, ShoppingBag, Loader, ArrowRight } from 'lucide-react';
import { API } from '../config/api';

const TrackingPage = () => {
  const [packetId, setPacketId] = useState('');
  const [journeyData, setJourneyData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [verificationSteps, setVerificationSteps] = useState([]);
  const [currentStep, setCurrentStep] = useState(-1);
  const [selectedStage, setSelectedStage] = useState(null);

  const steps = [
    'Data has been pushed to Blockchain',
    'Data successfully fetched from Blockchain',
    'Data successfully fetched from the database',
    'Hash generation from TC Data',
    'Hash Matched',
    'Data successfully verified'
  ];

  useEffect(() => {
    if (loading) {
      setVerificationSteps([]);
      setCurrentStep(-1);
      
      const interval = setInterval(() => {
        setCurrentStep(prev => {
          if (prev < steps.length - 1) {
            setVerificationSteps(prevSteps => [...prevSteps, steps[prev + 1]]);
            return prev + 1;
          }
          return prev;
        });
      }, 400);

      return () => clearInterval(interval);
    }
  }, [loading]);

  const handleTrack = async () => {
    if (!packetId.trim()) {
      setError('Please enter a packet ID');
      return;
    }

    setLoading(true);
    setError('');
    setJourneyData(null);
    setSelectedStage(null);
    
    try {
      const response = await API.getJourney(packetId);
      setJourneyData(response.data);
      // Auto-select first stage
      if (response.data.packet) {
        setSelectedStage('packet');
      }
    } catch (err) {
      setError(err.response?.data?.error || 'Failed to fetch tracking information');
      setJourneyData(null);
    } finally {
      setTimeout(() => setLoading(false), 2800);
    }
  };

  const mapLink = (value) => {
    if (!value || typeof value !== 'string') return null;
    const parts = value.split(',').map((s) => s.trim());
    if (parts.length !== 2) return null;
    const [lat, lng] = parts;
    return `https://www.google.com/maps?q=${encodeURIComponent(lat)},${encodeURIComponent(lng)}`;
  };

  const renderObject = (obj) => {
    return (
      <div className="space-y-3">
        {Object.entries(obj).map(([key, value]) => {
          if (typeof value === 'object' || value === null || value === '') return null;
          const isGps = key.toLowerCase().includes('gps') || key.toLowerCase().includes('coordinates') || key.toLowerCase().includes('location');
          const link = isGps ? mapLink(String(value)) : null;
          
          return (
            <div key={key} className="flex justify-between items-start py-3 border-b border-gray-200 last:border-0">
              <span className="text-sm font-semibold text-slate-600 uppercase tracking-wide">
                {key.replace(/_/g, ' ')}
              </span>
              {isGps && link ? (
                <a 
                  href={link} 
                  target="_blank" 
                  rel="noreferrer" 
                  className="text-emerald-600 hover:text-emerald-700 font-semibold inline-flex items-center gap-1 transition-colors"
                >
                  <MapPin className="w-4 h-4" />
                  <span className="underline">{String(value)}</span>
                </a>
              ) : (
                <span className="text-slate-900 font-semibold text-right max-w-xs">{value.toString()}</span>
              )}
            </div>
          );
        })}
      </div>
    );
  };

  const stageConfigs = [
    { 
      key: 'packet', 
      title: 'Packet Info', 
      fullTitle: 'Packet Information',
      icon: Package, 
      bgColor: 'bg-gradient-to-br from-slate-600 to-slate-800',
      lightBg: 'bg-slate-50',
      textColor: 'text-slate-700',
      borderColor: 'border-slate-300',
      iconBg: 'bg-slate-100'
    },
    { 
      key: 'harvest', 
      title: 'Harvest', 
      fullTitle: 'Harvest Details',
      icon: Sprout, 
      bgColor: 'bg-gradient-to-br from-green-500 to-emerald-600',
      lightBg: 'bg-green-50',
      textColor: 'text-green-700',
      borderColor: 'border-green-300',
      iconBg: 'bg-green-100'
    },
    { 
      key: 'processing', 
      title: 'Processing', 
      fullTitle: 'Processing & Quality Check',
      icon: Factory, 
      bgColor: 'bg-gradient-to-br from-violet-500 to-purple-600',
      lightBg: 'bg-violet-50',
      textColor: 'text-violet-700',
      borderColor: 'border-violet-300',
      iconBg: 'bg-violet-100'
    },
    { 
      key: 'distributor', 
      title: 'Distribution', 
      fullTitle: 'Distribution',
      icon: Truck, 
      bgColor: 'bg-gradient-to-br from-amber-500 to-orange-600',
      lightBg: 'bg-amber-50',
      textColor: 'text-amber-700',
      borderColor: 'border-amber-300',
      iconBg: 'bg-amber-100'
    },
    { 
      key: 'supplier', 
      title: 'Supplier', 
      fullTitle: 'Supplier',
      icon: Store, 
      bgColor: 'bg-gradient-to-br from-rose-500 to-pink-600',
      lightBg: 'bg-rose-50',
      textColor: 'text-rose-700',
      borderColor: 'border-rose-300',
      iconBg: 'bg-rose-100'
    },
    { 
      key: 'shopkeeper', 
      title: 'Retail', 
      fullTitle: 'Retail Store',
      icon: ShoppingBag, 
      bgColor: 'bg-gradient-to-br from-blue-500 to-indigo-600',
      lightBg: 'bg-blue-50',
      textColor: 'text-blue-700',
      borderColor: 'border-blue-300',
      iconBg: 'bg-blue-100'
    },
  ];

  const renderStageCard = (config, data, index) => {
    if (!data) return null;
    const Icon = config.icon;
    const isSelected = selectedStage === config.key;

    return (
      <div 
        key={config.key}
        onClick={() => setSelectedStage(isSelected ? null : config.key)}
        className={`cursor-pointer transition-all duration-300 rounded-xl ${
          isSelected 
            ? 'ring-4 ring-offset-2 shadow-2xl ' + config.borderColor.replace('border', 'ring') 
            : 'hover:shadow-lg shadow-md'
        }`}
        style={{ 
          animation: 'slideIn 0.5s ease-out forwards',
          animationDelay: `${index * 100}ms`,
          opacity: 0
        }}
      >
        <div className={`${config.bgColor} px-6 py-5 flex items-center gap-4 rounded-t-xl ${!isSelected ? 'rounded-b-xl' : ''}`}>
          <div className="bg-white bg-opacity-20 backdrop-blur-sm p-3 rounded-xl">
            <Icon className="w-7 h-7 text-white" />
          </div>
          <div className="flex-1">
            <h3 className="text-xl font-bold text-white">{config.fullTitle}</h3>
          </div>
          <div className="bg-white bg-opacity-25 backdrop-blur-sm px-4 py-2 rounded-full">
            <span className="text-sm font-bold text-white">Stage {index + 1}</span>
          </div>
        </div>
        {isSelected && (
          <div className={`${config.lightBg} p-6 border-x-2 border-b-2 ${config.borderColor} rounded-b-xl`}>
            {renderObject(data)}
          </div>
        )}
      </div>
    );
  };

  const timeline = useMemo(() => {
    const timelineSteps = [
      { key: 'harvest', title: 'Harvest', icon: Sprout },
      { key: 'processing', title: 'Processing', icon: Factory },
      { key: 'distributor', title: 'Distribution', icon: Truck },
      { key: 'supplier', title: 'Supplier', icon: Store },
      { key: 'shopkeeper', title: 'Retail', icon: ShoppingBag },
    ];
    const present = journeyData || {};
    return timelineSteps.map((s) => ({
      ...s,
      done: Boolean(present[s.key]),
    }));
  }, [journeyData]);

  const VerificationScreen = () => (
    <div className="fixed inset-0 bg-gradient-to-br from-slate-900 via-slate-800 to-slate-900 z-50 flex items-center justify-center">
      <div className="max-w-md w-full mx-4">
        <div className="text-center mb-8">
          <div className="inline-block p-4 bg-emerald-500 bg-opacity-20 backdrop-blur-sm rounded-full mb-4">
            <Loader className="w-12 h-12 text-emerald-400 animate-spin" />
          </div>
          <h2 className="text-2xl font-bold text-white mb-2">Verifying on Blockchain...</h2>
          <p className="text-slate-300">Tracking packet: <span className="font-semibold text-emerald-400">{packetId}</span></p>
        </div>

        <div className="space-y-3">
          {verificationSteps.map((step, index) => (
            <div 
              key={index}
              className="flex items-start gap-3 p-4 bg-white bg-opacity-10 backdrop-blur-md rounded-xl border border-white border-opacity-20"
              style={{
                animation: 'fadeIn 0.3s ease-out forwards',
                opacity: 0
              }}
            >
              <div className="flex-shrink-0 mt-0.5">
                <CheckCircle className="w-6 h-6 text-emerald-400" />
              </div>
              <p className="text-white font-medium">{step}</p>
            </div>
          ))}
        </div>

        {currentStep === steps.length - 1 && (
          <button
            className="w-full mt-6 px-6 py-4 bg-gradient-to-r from-emerald-500 to-green-600 text-white rounded-xl font-bold text-lg hover:from-emerald-600 hover:to-green-700 transition-all shadow-lg"
            style={{
              animation: 'fadeIn 0.3s ease-out forwards',
              opacity: 0,
              animationDelay: '200ms'
            }}
          >
            Trace successfully verified
          </button>
        )}
      </div>
    </div>
  );

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 via-gray-50 to-slate-100 py-8 px-4">
      {loading && <VerificationScreen />}

      <div className="max-w-7xl mx-auto">
        {/* Header */}
        <div className="text-center mb-10">
          <h1 className="text-5xl font-extrabold bg-gradient-to-r from-slate-800 to-slate-600 bg-clip-text text-transparent mb-3">
            Track Your Product
          </h1>
          <p className="text-slate-600 text-lg">Enter packet ID to trace the complete supply chain journey</p>
        </div>

        {/* Search Box */}
        <div className="bg-white rounded-2xl shadow-xl p-6 mb-10 border border-slate-200">
          <div className="flex flex-col md:flex-row gap-4">
            <input
              type="text"
              value={packetId}
              onChange={(e) => setPacketId(e.target.value)}
              onKeyPress={(e) => e.key === 'Enter' && handleTrack()}
              placeholder="Enter Packet ID"
              className="flex-1 px-6 py-4 border-2 border-slate-300 rounded-xl focus:border-emerald-500 focus:outline-none text-lg transition-colors"
            />
            <button
              onClick={handleTrack}
              disabled={loading}
              className="px-10 py-4 bg-gradient-to-r from-emerald-500 to-green-600 text-white rounded-xl hover:from-emerald-600 hover:to-green-700 disabled:from-gray-400 disabled:to-gray-500 disabled:cursor-not-allowed flex items-center justify-center gap-2 transition-all font-semibold text-lg shadow-lg hover:shadow-xl"
            >
              <Search className="w-5 h-5" />
              Track
            </button>
          </div>
          
          {error && (
            <div className="mt-4 p-4 bg-red-50 border-2 border-red-300 rounded-xl flex items-center gap-3 text-red-700">
              <AlertCircle className="w-5 h-5 flex-shrink-0" />
              <span className="font-medium">{error}</span>
            </div>
          )}
        </div>

        {/* Results */}
        {journeyData && !loading && (
          <div className="space-y-8">
            {/* Timeline */}
            <div className="bg-white rounded-2xl shadow-xl p-8 border border-emerald-200">
              <div className="flex items-center gap-3 text-emerald-600 mb-8">
                <div className="bg-emerald-100 p-2 rounded-lg">
                  <CheckCircle className="w-7 h-7" />
                </div>
                <span className="font-bold text-xl">Journey Verified on Blockchain</span>
              </div>
              
              <div className="flex items-center justify-between overflow-x-auto pb-4">
                {timeline.map((step, idx) => {
                  const Icon = step.icon;
                  const config = stageConfigs.find(s => s.key === step.key);
                  return (
                    <React.Fragment key={step.key}>
                      <div 
                        onClick={() => step.done && setSelectedStage(step.key)}
                        className={`flex flex-col items-center min-w-[100px] ${step.done ? 'cursor-pointer' : ''}`}
                      >
                        <div className={`w-16 h-16 rounded-2xl flex items-center justify-center mb-3 transition-all shadow-md ${
                          step.done 
                            ? selectedStage === step.key 
                              ? config?.bgColor + ' scale-110 shadow-lg' 
                              : config?.bgColor + ' hover:scale-105' 
                            : 'bg-slate-200'
                        }`}>
                          <Icon className={`w-8 h-8 ${step.done ? 'text-white' : 'text-slate-400'}`} />
                        </div>
                        <span className={`text-sm font-bold text-center ${
                          step.done 
                            ? selectedStage === step.key 
                              ? config?.textColor 
                              : 'text-slate-700' 
                            : 'text-slate-400'
                        }`}>
                          {step.title}
                        </span>
                      </div>
                      {idx < timeline.length - 1 && (
                        <div className="flex-1 mx-2 min-w-[40px]">
                          <div className={`h-2 rounded-full transition-all ${
                            step.done && timeline[idx + 1].done 
                              ? 'bg-gradient-to-r from-emerald-500 to-green-500' 
                              : 'bg-slate-200'
                          }`}></div>
                        </div>
                      )}
                    </React.Fragment>
                  );
                })}
              </div>
            </div>

            {/* Instructions */}
            <div className="bg-gradient-to-r from-blue-50 to-indigo-50 rounded-2xl p-6 border border-blue-200">
              <div className="flex items-start gap-3">
                <div className="bg-blue-500 p-2 rounded-lg mt-1">
                  <ArrowRight className="w-5 h-5 text-white" />
                </div>
                <div>
                  <h3 className="font-bold text-slate-800 text-lg mb-1">Click on any stage to view details</h3>
                  <p className="text-slate-600">Select a stage from the timeline above or click on the cards below to expand and see complete information</p>
                </div>
              </div>
            </div>

            {/* Stage Cards */}
            <div className="space-y-4">
              {stageConfigs.map((config, index) => 
                journeyData[config.key] && renderStageCard(config, journeyData[config.key], index)
              )}
            </div>
          </div>
        )}

        {/* Empty State */}
        {!journeyData && !loading && !error && (
          <div className="text-center py-20 bg-white rounded-2xl shadow-xl border border-slate-200">
            <div className="bg-slate-100 w-24 h-24 rounded-2xl flex items-center justify-center mx-auto mb-6">
              <Package className="w-12 h-12 text-slate-400" />
            </div>
            <h3 className="text-2xl font-bold text-slate-800 mb-2">Ready to Track</h3>
            <p className="text-slate-600 text-lg">Enter a packet ID above to start tracking your product's journey</p>
          </div>
        )}
      </div>

      <style>{`
        @keyframes slideIn {
          from {
            opacity: 0;
            transform: translateY(20px);
          }
          to {
            opacity: 1;
            transform: translateY(0);
          }
        }
        
        @keyframes fadeIn {
          from {
            opacity: 0;
          }
          to {
            opacity: 1;
          }
        }
      `}</style>
    </div>
  );
};

export default TrackingPage;
