import React, { useMemo, useState, useEffect } from 'react';
import { Search, Package, MapPin, CheckCircle, AlertCircle, Clock, ChevronRight, Sprout, Factory, Truck, Store, ShoppingBag, Loader } from 'lucide-react';

// Simulated API for demo
const API = {
  getJourney: async (packetId) => {
    await new Promise(resolve => setTimeout(resolve, 2000));
    if (packetId.toUpperCase() === 'PKT001') {
      return {
        data: {
          packet: {
            packet_id: 'PKT001',
            product_name: 'Premium Turmeric Powder',
            batch_number: 'TURM-2024-001',
            quantity: '500g',
            manufacturing_date: '2024-10-01'
          },
          harvest: {
            farmer_name: 'Rajesh Kumar',
            farm_location: 'Sangli, Maharashtra',
            harvest_date: '2024-09-15',
            gps_coordinates: '16.8524,74.5815',
            soil_type: 'Red Laterite',
            organic_certified: 'Yes'
          },
          processing: {
            processing_unit: 'Golden Spice Mills',
            processing_date: '2024-09-18',
            quality_grade: 'Grade A',
            curcumin_content: '4.5%',
            moisture_level: '10%',
            gps_location: '16.8600,74.5900'
          },
          distributor: {
            distributor_name: 'Maharashtra Agro Distributors',
            location: 'Pune, Maharashtra',
            received_date: '2024-09-22',
            temperature_maintained: '15-20Â°C',
            gps_coordinates: '18.5204,73.8567'
          },
          supplier: {
            supplier_name: 'Fresh Spice Wholesalers',
            location: 'Mumbai, Maharashtra',
            received_date: '2024-09-28',
            storage_conditions: 'Climate Controlled',
            gps_coordinates: '19.0760,72.8777'
          },
          shopkeeper: {
            store_name: 'Green Valley Organic Store',
            location: 'Nashik, Maharashtra',
            shelf_date: '2024-10-05',
            expiry_date: '2025-10-01',
            gps_coordinates: '19.9975,73.7898'
          }
        }
      };
    }
    throw { response: { data: { error: 'Packet ID not found' } } };
  }
};

const TrackingPage = () => {
  const [packetId, setPacketId] = useState('');
  const [journeyData, setJourneyData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [animateCards, setAnimateCards] = useState(false);
  const [processingStage, setProcessingStage] = useState(0);

  useEffect(() => {
    if (journeyData) {
      setAnimateCards(true);
    }
  }, [journeyData]);

  const handleTrack = async () => {
    if (!packetId.trim()) {
      setError('Please enter a packet ID');
      return;
    }

    setLoading(true);
    setError('');
    setJourneyData(null);
    setAnimateCards(false);
    setProcessingStage(0);
    
    // Simulate processing stages
    const stages = ['Connecting to blockchain...', 'Fetching harvest data...', 'Loading supply chain...', 'Verifying authenticity...'];
    let currentStage = 0;
    const stageInterval = setInterval(() => {
      if (currentStage < stages.length) {
        setProcessingStage(currentStage);
        currentStage++;
      }
    }, 500);
    
    try {
      const response = await API.getJourney(packetId);
      clearInterval(stageInterval);
      setJourneyData(response.data);
      setProcessingStage(stages.length);
    } catch (err) {
      clearInterval(stageInterval);
      setError(err.response?.data?.error || 'Failed to fetch tracking information');
      setJourneyData(null);
    } finally {
      setLoading(false);
    }
  };

  const mapLink = (value) => {
    if (!value || typeof value !== 'string') return null;
    const parts = value.split(',').map((s) => s.trim());
    if (parts.length !== 2) return null;
    const [lat, lng] = parts;
    return `https://www.google.com/maps?q=${encodeURIComponent(lat)},${encodeURIComponent(lng)}`;
  };

  const renderObject = (obj, accentColor) => {
    return (
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        {Object.entries(obj).map(([key, value], idx) => {
          if (typeof value === 'object' || value === null || value === '') return null;
          const isGps = key.toLowerCase().includes('gps') || key.toLowerCase().includes('coordinates');
          const link = isGps ? mapLink(String(value)) : null;
          return (
            <div 
              key={key} 
              className={`border-l-4 border-${accentColor}-400 pl-4 py-2 rounded-r transition-all duration-300 hover:bg-${accentColor}-50 hover:shadow-md`}
              style={{ animationDelay: `${idx * 50}ms` }}
            >
              <p className="text-xs font-semibold text-gray-500 uppercase tracking-wider mb-1">
                {key.replace(/_/g, ' ')}
              </p>
              {isGps && link ? (
                <a 
                  href={link} 
                  target="_blank" 
                  rel="noreferrer" 
                  className={`text-${accentColor}-600 hover:text-${accentColor}-700 font-medium inline-flex items-center gap-1 transition-colors`}
                >
                  <MapPin className="w-4 h-4" />
                  <span className="underline">{String(value)}</span>
                </a>
              ) : (
                <p className="text-gray-800 font-medium">{value.toString()}</p>
              )}
            </div>
          );
        })}
      </div>
    );
  };

  const stageConfigs = [
    { key: 'packet', title: 'Packet Information', icon: Package, color: 'amber', gradient: 'from-amber-500 to-orange-500' },
    { key: 'harvest', title: 'Harvest Details', icon: Sprout, color: 'green', gradient: 'from-green-500 to-emerald-600' },
    { key: 'processing', title: 'Processing & Quality Check', icon: Factory, color: 'blue', gradient: 'from-blue-500 to-cyan-600' },
    { key: 'distributor', title: 'Distribution', icon: Truck, color: 'purple', gradient: 'from-purple-500 to-pink-600' },
    { key: 'supplier', title: 'Supplier', icon: Store, color: 'orange', gradient: 'from-orange-500 to-red-500' },
    { key: 'shopkeeper', title: 'Retail Store', icon: ShoppingBag, color: 'rose', gradient: 'from-rose-500 to-pink-600' },
  ];

  const renderStageCard = (config, data, index) => {
    if (!data) return null;
    const Icon = config.icon;

    return (
      <div 
        key={config.key}
        className={`bg-white rounded-2xl shadow-lg overflow-hidden border border-gray-100 transform transition-all duration-500 hover:shadow-2xl hover:-translate-y-1 ${animateCards ? 'animate-slide-up opacity-100' : 'opacity-0'}`}
        style={{ animationDelay: `${index * 150}ms` }}
      >
        <div className={`bg-gradient-to-r ${config.gradient} p-6 text-white`}>
          <div className="flex items-center gap-3">
            <div className="bg-white bg-opacity-20 p-3 rounded-xl backdrop-blur-sm">
              <Icon className="w-6 h-6" />
            </div>
            <div>
              <h3 className="text-xl font-bold">{config.title}</h3>
              <p className="text-sm text-white text-opacity-90">Stage {index + 1} of 6</p>
            </div>
          </div>
        </div>
        <div className="p-6">
          {renderObject(data, config.color)}
        </div>
      </div>
    );
  };

  const timeline = useMemo(() => {
    const steps = [
      { key: 'harvest', title: 'Harvested', icon: Sprout },
      { key: 'processing', title: 'Processed', icon: Factory },
      { key: 'distributor', title: 'Distributed', icon: Truck },
      { key: 'supplier', title: 'Supplied', icon: Store },
      { key: 'shopkeeper', title: 'In Store', icon: ShoppingBag },
    ];
    const present = journeyData || {};
    return steps.map((s) => ({
      ...s,
      done: Boolean(present[s.key]),
    }));
  }, [journeyData]);

  const ProcessingAnimation = () => (
    <div className="bg-white rounded-2xl shadow-xl p-12 border border-gray-100">
      <div className="flex flex-col items-center justify-center space-y-6">
        <div className="relative">
          <div className="w-24 h-24 border-4 border-amber-200 rounded-full animate-pulse"></div>
          <div className="absolute inset-0 w-24 h-24 border-4 border-t-amber-500 rounded-full animate-spin"></div>
          <Loader className="absolute inset-0 m-auto w-10 h-10 text-amber-600 animate-pulse" />
        </div>
        <div className="text-center">
          <h3 className="text-2xl font-bold text-gray-800 mb-2">Tracking Your Product</h3>
          <p className="text-gray-600 animate-pulse">
            {['Connecting to blockchain...', 'Fetching harvest data...', 'Loading supply chain...', 'Verifying authenticity...'][processingStage] || 'Processing...'}
          </p>
        </div>
        <div className="w-64 h-2 bg-gray-200 rounded-full overflow-hidden">
          <div 
            className="h-full bg-gradient-to-r from-amber-500 to-orange-500 transition-all duration-500 rounded-full"
            style={{ width: `${(processingStage / 4) * 100}%` }}
          ></div>
        </div>
      </div>
    </div>
  );

  return (
    <div className="min-h-screen bg-gradient-to-br from-amber-50 via-orange-50 to-yellow-50 py-8 px-4">
      <div className="max-w-7xl mx-auto">
        {/* Header */}
        <div className="bg-gradient-to-r from-amber-600 via-orange-600 to-yellow-600 rounded-3xl shadow-2xl p-10 mb-8 relative overflow-hidden">
          <div className="absolute inset-0 bg-black opacity-10"></div>
          <div className="absolute top-0 right-0 w-64 h-64 bg-white opacity-10 rounded-full -mr-32 -mt-32"></div>
          <div className="absolute bottom-0 left-0 w-48 h-48 bg-white opacity-10 rounded-full -ml-24 -mb-24"></div>
          <div className="relative z-10">
            <div className="flex items-center gap-4 mb-4">
              <div className="bg-white bg-opacity-20 p-4 rounded-2xl backdrop-blur-sm">
                <Package className="w-10 h-10 text-white" />
              </div>
              <div>
                <h1 className="text-4xl font-bold text-white mb-2">Turmeric Supply Chain Tracker</h1>
                <p className="text-amber-100 text-lg">Track your product's journey from farm to store</p>
              </div>
            </div>
          </div>
        </div>

        {/* Search Form */}
        <div className="bg-white rounded-2xl shadow-xl p-6 mb-8 border border-gray-100">
          <div className="flex flex-col md:flex-row gap-4">
            <div className="flex-1">
              <input
                type="text"
                value={packetId}
                onChange={(e) => setPacketId(e.target.value)}
                onKeyPress={(e) => e.key === 'Enter' && handleTrack()}
                placeholder="Enter Packet ID (Try: PKT001)"
                className="w-full px-6 py-4 border-2 border-gray-200 rounded-xl focus:ring-2 focus:ring-amber-500 focus:border-amber-500 transition-all text-lg"
              />
            </div>
            <button
              onClick={handleTrack}
              disabled={loading}
              className="px-10 py-4 bg-gradient-to-r from-amber-500 to-orange-600 text-white rounded-xl hover:from-amber-600 hover:to-orange-700 disabled:from-gray-400 disabled:to-gray-500 disabled:cursor-not-allowed flex items-center justify-center gap-3 transition-all shadow-lg hover:shadow-xl transform hover:-translate-y-0.5 font-semibold text-lg"
            >
              <Search className="w-6 h-6" />
              {loading ? 'Tracking...' : 'Track Package'}
            </button>
          </div>
          
          {error && (
            <div className="mt-4 p-4 bg-red-50 border-2 border-red-200 rounded-xl flex items-center gap-3 text-red-700 animate-shake">
              <AlertCircle className="w-6 h-6 flex-shrink-0" />
              <span className="font-medium">{error}</span>
            </div>
          )}
        </div>

        {/* Loading State */}
        {loading && <ProcessingAnimation />}

        {/* Results */}
        {journeyData && !loading && (
          <div className="space-y-6">
            {/* Timeline */}
            <div className="bg-white rounded-2xl shadow-xl p-6 border border-gray-100">
              <div className="flex items-center gap-3 text-green-700 mb-6">
                <CheckCircle className="w-6 h-6" />
                <span className="font-bold text-lg">Journey Verified â</span>
              </div>
              <div className="flex items-center justify-between overflow-x-auto pb-4">
                {timeline.map((step, idx) => {
                  const Icon = step.icon;
                  return (
                    <React.Fragment key={step.key}>
                      <div className={`flex flex-col items-center transition-all duration-500 ${step.done ? 'scale-100' : 'scale-90 opacity-50'}`}>
                        <div className={`w-16 h-16 rounded-full flex items-center justify-center mb-2 transition-all duration-500 ${step.done ? 'bg-gradient-to-br from-green-500 to-emerald-600 shadow-lg' : 'bg-gray-200'}`}>
                          <Icon className={`w-8 h-8 ${step.done ? 'text-white' : 'text-gray-400'}`} />
                        </div>
                        <span className={`text-sm font-semibold text-center whitespace-nowrap ${step.done ? 'text-green-700' : 'text-gray-400'}`}>
                          {step.title}
                        </span>
                      </div>
                      {idx < timeline.length - 1 && (
                        <div className={`flex-1 h-1 mx-2 rounded transition-all duration-500 ${step.done && timeline[idx + 1].done ? 'bg-gradient-to-r from-green-500 to-emerald-600' : 'bg-gray-200'}`}></div>
                      )}
                    </React.Fragment>
                  );
                })}
              </div>
            </div>

            {/* Stage Cards */}
            <div className="space-y-6">
              {stageConfigs.map((config, index) => 
                journeyData[config.key] && renderStageCard(config, journeyData[config.key], index)
              )}
            </div>
          </div>
        )}

        {/* Empty State */}
        {!journeyData && !loading && !error && (
          <div className="text-center py-20 bg-white rounded-2xl shadow-xl border border-gray-100">
            <div className="w-32 h-32 bg-gradient-to-br from-amber-100 to-orange-100 rounded-full flex items-center justify-center mx-auto mb-6">
              <Package className="w-16 h-16 text-amber-600" />
            </div>
            <h3 className="text-2xl font-bold text-gray-800 mb-2">Ready to Track</h3>
            <p className="text-gray-600 text-lg">Enter a packet ID above to view its complete journey</p>
          </div>
        )}
      </div>

      <style>{`
        @keyframes slide-up {
          from {
            opacity: 0;
            transform: translateY(30px);
          }
          to {
            opacity: 1;
            transform: translateY(0);
          }
        }
        
        @keyframes shake {
          0%, 100% { transform: translateX(0); }
          25% { transform: translateX(-5px); }
          75% { transform: translateX(5px); }
        }
        
        .animate-slide-up {
          animation: slide-up 0.6s ease-out forwards;
        }
        
        .animate-shake {
          animation: shake 0.4s ease-in-out;
        }
      `}</style>
    </div>
     );
};

export default TrackingPage;
