import React, { useMemo, useState, useEffect } from 'react';
import { Search, Package, MapPin, CheckCircle, AlertCircle, Sprout, Factory, Truck, Store, ShoppingBag, Loader, ArrowRight } from 'lucide-react';
import { API } from '../config/api';

const TrackingPage = () => {
  const [packetId, setPacketId] = useState('');
  const [journeyData, setJourneyData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [verificationSteps, setVerificationSteps] = useState([]);
  const [currentStep, setCurrentStep] = useState(-1);

  const steps = [
    'Data has been pushed to Blockchain',
    'Data successfully fetched from Blockchain',
    'Data successfully fetched from the database',
    'Hash generation from TC Data',
    'Hash Matched',
    'Data successfully verified'
  ];

  useEffect(() => {
    if (loading) {
      setVerificationSteps([]);
      setCurrentStep(-1);
      
      const interval = setInterval(() => {
        setCurrentStep(prev => {
          if (prev < steps.length - 1) {
            setVerificationSteps(prevSteps => [...prevSteps, steps[prev + 1]]);
            return prev + 1;
          }
          return prev;
        });
      }, 400);

      return () => clearInterval(interval);
    }
  }, [loading]);

  const handleTrack = async () => {
    if (!packetId.trim()) {
      setError('Please enter a packet ID');
      return;
    }

    setLoading(true);
    setError('');
    setJourneyData(null);
    
    try {
      const response = await API.getJourney(packetId);
      setJourneyData(response.data);
    } catch (err) {
      setError(err.response?.data?.error || 'Failed to fetch tracking information');
      setJourneyData(null);
    } finally {
      setTimeout(() => setLoading(false), 2800);
    }
  };

  const mapLink = (value) => {
    if (!value || typeof value !== 'string') return null;
    const parts = value.split(',').map((s) => s.trim());
    if (parts.length !== 2) return null;
    const [lat, lng] = parts;
    return `https://www.google.com/maps?q=${encodeURIComponent(lat)},${encodeURIComponent(lng)}`;
  };

  const renderObject = (obj) => {
    return (
      <div className="space-y-3">
        {Object.entries(obj).map(([key, value]) => {
          if (typeof value === 'object' || value === null || value === '') return null;
          const isGps = key.toLowerCase().includes('gps') || key.toLowerCase().includes('coordinates') || key.toLowerCase().includes('location');
          const link = isGps ? mapLink(String(value)) : null;
          
          return (
            <div key={key} className="flex justify-between items-start py-2 border-b border-gray-100 last:border-0">
              <span className="text-sm font-medium text-gray-600 uppercase tracking-wide">
                {key.replace(/_/g, ' ')}
              </span>
              {isGps && link ? (
                <a 
                  href={link} 
                  target="_blank" 
                  rel="noreferrer" 
                  className="text-emerald-600 hover:text-emerald-700 font-semibold inline-flex items-center gap-1 transition-colors"
                >
                  <MapPin className="w-4 h-4" />
                  <span className="underline">{String(value)}</span>
                </a>
              ) : (
                <span className="text-gray-900 font-semibold text-right max-w-xs">{value.toString()}</span>
              )}
            </div>
          );
        })}
      </div>
    );
  };

  const stageConfigs = [
    { 
      key: 'packet', 
      title: 'Packet Information', 
      icon: Package, 
      bgColor: 'bg-blue-500',
      textColor: 'text-blue-600',
      borderColor: 'border-blue-200'
    },
    { 
      key: 'harvest', 
      title: 'Harvest Details', 
      icon: Sprout, 
      bgColor: 'bg-emerald-500',
      textColor: 'text-emerald-600',
      borderColor: 'border-emerald-200'
    },
    { 
      key: 'processing', 
      title: 'Processing & Quality Check', 
      icon: Factory, 
      bgColor: 'bg-purple-500',
      textColor: 'text-purple-600',
      borderColor: 'border-purple-200'
    },
    { 
      key: 'distributor', 
      title: 'Distribution', 
      icon: Truck, 
      bgColor: 'bg-orange-500',
      textColor: 'text-orange-600',
      borderColor: 'border-orange-200'
    },
    { 
      key: 'supplier', 
      title: 'Supplier', 
      icon: Store, 
      bgColor: 'bg-pink-500',
      textColor: 'text-pink-600',
      borderColor: 'border-pink-200'
    },
    { 
      key: 'shopkeeper', 
      title: 'Retail Store', 
      icon: ShoppingBag, 
      bgColor: 'bg-indigo-500',
      textColor: 'text-indigo-600',
      borderColor: 'border-indigo-200'
    },
  ];

  const renderStageCard = (config, data, index) => {
    if (!data) return null;
    const Icon = config.icon;

    return (
      <div 
        key={config.key}
        className="bg-white rounded-lg border-2 border-gray-100 overflow-hidden shadow-sm hover:shadow-md transition-all duration-300"
        style={{ 
          animation: 'slideIn 0.5s ease-out forwards',
          animationDelay: `${index * 100}ms`,
          opacity: 0
        }}
      >
        <div className={`${config.bgColor} px-6 py-4 flex items-center gap-3`}>
          <div className="bg-white bg-opacity-30 p-2 rounded-lg">
            <Icon className="w-6 h-6 text-white" />
          </div>
          <div className="flex-1">
            <h3 className="text-lg font-bold text-white">{config.title}</h3>
          </div>
          <div className="bg-white bg-opacity-20 px-3 py-1 rounded-full">
            <span className="text-xs font-bold text-white">Stage {index + 1}</span>
          </div>
        </div>
        <div className="p-6">
          {renderObject(data)}
        </div>
      </div>
    );
  };

  const timeline = useMemo(() => {
    const timelineSteps = [
      { key: 'harvest', title: 'Harvest', icon: Sprout },
      { key: 'processing', title: 'Processing', icon: Factory },
      { key: 'distributor', title: 'Distribution', icon: Truck },
      { key: 'supplier', title: 'Supplier', icon: Store },
      { key: 'shopkeeper', title: 'Retail', icon: ShoppingBag },
    ];
    const present = journeyData || {};
    return timelineSteps.map((s) => ({
      ...s,
      done: Boolean(present[s.key]),
    }));
  }, [journeyData]);

  const VerificationScreen = () => (
    <div className="fixed inset-0 bg-white z-50 flex items-center justify-center">
      <div className="max-w-md w-full mx-4">
        <div className="text-center mb-8">
          <div className="inline-block p-4 bg-emerald-100 rounded-full mb-4">
            <Loader className="w-12 h-12 text-emerald-600 animate-spin" />
          </div>
          <h2 className="text-2xl font-bold text-gray-800 mb-2">Verifying on Blockchain...</h2>
          <p className="text-gray-600">Tracking packet: <span className="font-semibold">{packetId}</span></p>
        </div>

        <div className="space-y-3">
          {verificationSteps.map((step, index) => (
            <div 
              key={index}
              className="flex items-start gap-3 p-4 bg-white rounded-lg border-2 border-gray-100"
              style={{
                animation: 'fadeIn 0.3s ease-out forwards',
                opacity: 0
              }}
            >
              <div className="flex-shrink-0 mt-0.5">
                <CheckCircle className="w-6 h-6 text-emerald-500" />
              </div>
              <p className="text-gray-700 font-medium">{step}</p>
            </div>
          ))}
        </div>

        {currentStep === steps.length - 1 && (
          <button
            className="w-full mt-6 px-6 py-4 bg-emerald-500 text-white rounded-lg font-bold text-lg hover:bg-emerald-600 transition-colors"
            style={{
              animation: 'fadeIn 0.3s ease-out forwards',
              opacity: 0,
              animationDelay: '200ms'
            }}
          >
            Trace successfully verified
          </button>
        )}
      </div>
    </div>
  );

  return (
    <div className="min-h-screen bg-gray-50 py-8 px-4">
      {loading && <VerificationScreen />}

      <div className="max-w-6xl mx-auto">
        {/* Header */}
        <div className="text-center mb-8">
          <h1 className="text-4xl font-bold text-gray-900 mb-2">Track Your Product</h1>
          <p className="text-gray-600 text-lg">Enter packet ID to trace the complete supply chain journey</p>
        </div>

        {/* Search Box */}
        <div className="bg-white rounded-lg shadow-md p-6 mb-8">
          <div className="flex flex-col md:flex-row gap-4">
            <input
              type="text"
              value={packetId}
              onChange={(e) => setPacketId(e.target.value)}
              onKeyPress={(e) => e.key === 'Enter' && handleTrack()}
              placeholder="Enter Packet ID"
              className="flex-1 px-4 py-3 border-2 border-gray-300 rounded-lg focus:border-emerald-500 focus:outline-none text-lg"
            />
            <button
              onClick={handleTrack}
              disabled={loading}
              className="px-8 py-3 bg-emerald-500 text-white rounded-lg hover:bg-emerald-600 disabled:bg-gray-400 disabled:cursor-not-allowed flex items-center justify-center gap-2 transition-colors font-semibold text-lg"
            >
              <Search className="w-5 h-5" />
              Track
            </button>
          </div>
          
          {error && (
            <div className="mt-4 p-4 bg-red-50 border-2 border-red-200 rounded-lg flex items-center gap-3 text-red-700">
              <AlertCircle className="w-5 h-5 flex-shrink-0" />
              <span className="font-medium">{error}</span>
            </div>
          )}
        </div>

        {/* Results */}
        {journeyData && !loading && (
          <div className="space-y-6">
            {/* Timeline */}
            <div className="bg-white rounded-lg shadow-md p-6 border-2 border-emerald-200">
              <div className="flex items-center gap-2 text-emerald-600 mb-6">
                <CheckCircle className="w-6 h-6" />
                <span className="font-bold text-lg">Journey Verified</span>
              </div>
              
              <div className="flex items-center justify-between">
                {timeline.map((step, idx) => {
                  const Icon = step.icon;
                  return (
                    <React.Fragment key={step.key}>
                      <div className="flex flex-col items-center">
                        <div className={`w-14 h-14 rounded-full flex items-center justify-center mb-2 transition-all ${step.done ? 'bg-emerald-500 shadow-lg' : 'bg-gray-200'}`}>
                          <Icon className={`w-7 h-7 ${step.done ? 'text-white' : 'text-gray-400'}`} />
                        </div>
                        <span className={`text-xs font-semibold text-center ${step.done ? 'text-emerald-600' : 'text-gray-400'}`}>
                          {step.title}
                        </span>
                      </div>
                      {idx < timeline.length - 1 && (
                        <div className="flex-1 mx-2">
                          <div className={`h-1 rounded transition-all ${step.done && timeline[idx + 1].done ? 'bg-emerald-500' : 'bg-gray-200'}`}></div>
                        </div>
                      )}
                    </React.Fragment>
                  );
                })}
              </div>
            </div>

            {/* Stage Cards */}
            <div className="space-y-4">
              {stageConfigs.map((config, index) => 
                journeyData[config.key] && renderStageCard(config, journeyData[config.key], index)
              )}
            </div>
          </div>
        )}

        {/* Empty State */}
        {!journeyData && !loading && !error && (
          <div className="text-center py-20 bg-white rounded-lg shadow-md">
            <Package className="w-20 h-20 text-gray-300 mx-auto mb-4" />
            <h3 className="text-xl font-bold text-gray-800 mb-2">Ready to Track</h3>
            <p className="text-gray-600">Enter a packet ID to start tracking</p>
          </div>
        )}
      </div>

      <style>{`
        @keyframes slideIn {
          from {
            opacity: 0;
            transform: translateY(20px);
          }
          to {
            opacity: 1;
            transform: translateY(0);
          }
        }
        
        @keyframes fadeIn {
          from {
            opacity: 0;
          }
          to {
            opacity: 1;
          }
        }
      `}</style>
    </div>
  );
};

export default TrackingPage;
