import React, { useState } from 'react';
import { MessageCircle, X } from 'lucide-react';

const CHATBOT_URL = 'https://landbot.online/v3/H-3337203-VVIJPE3ZMLNBWXO7/index.html';

function ChatbotWidget() {
  const [isOpen, setIsOpen] = useState(false);

  return (
    <>
      {/* Chatbot container - fixed bottom-right */}
      <div className="fixed bottom-6 right-6 z-[9999] flex flex-col items-end gap-2">
        {/* Chat window */}
        {isOpen && (
          <div
            className="bg-white rounded-2xl shadow-2xl border border-gray-200 overflow-hidden flex flex-col"
            style={{ width: '380px', height: '550px' }}
          >
            <div className="bg-gradient-to-r from-amber-500 to-amber-600 px-4 py-3 flex items-center justify-between">
              <div className="flex items-center gap-2">
                <MessageCircle className="text-white" size={24} />
                <span className="font-semibold text-white">Help & Navigation</span>
              </div>
              <button
                onClick={() => setIsOpen(false)}
                className="p-1.5 rounded-lg hover:bg-amber-600/80 transition-colors text-white"
                aria-label="Close chatbot"
              >
                <X size={22} />
              </button>
            </div>
            <iframe
              src={CHATBOT_URL}
              title="Turmeric Supply Chain Assistant"
              className="flex-1 w-full border-0"
              style={{ minHeight: '480px' }}
            />
          </div>
        )}

        {/* Toggle button */}
        <button
          onClick={() => setIsOpen(!isOpen)}
          className="flex items-center justify-center w-14 h-14 rounded-full bg-gradient-to-br from-amber-500 to-amber-600 text-white shadow-lg hover:shadow-xl hover:scale-105 transition-all duration-200"
          aria-label={isOpen ? 'Close chatbot' : 'Open chatbot'}
        >
          <MessageCircle size={28} />
        </button>
      </div>
    </>
  );
}

export default ChatbotWidget;
